import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {

  const [data, setData] = useState(null);

  const handleChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value })
  }

  const handlePost = (e) => {
    e.preventDefault();
    axios.post('/api/order', data)
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
  }

  return (
    <div >
      <form>
        <input type="text" name='customerName' placeholder='Name' onChange={handleChange} /><br />
        <input type="text" name='customerNum' placeholder='Number' onChange={handleChange} /><br />
        <input type="text" name='deliveryAddress' placeholder='Addrss' onChange={handleChange} /><br />
        <button onClick={handlePost}>Post</button>
      </form >
    </div>
  );

}

export default App;
